      withCredentials: true,
      withXSRFToken: true
    },
    'profile|shop.mysite.com': {},
    '*.mysite.com': {},
    '**.mysite.com': {
      withXSRFToken: true
    }, // for all subdomains
    'https://**': { // for all https requests
      withCredentials: true
    }
  }  
})
```

