-                builder.Add((option & RefsFilter.Remotes) != 0, "refs/remotes/");
-                builder.Add((option & RefsFilter.Tags) != 0, "refs/tags/");
+                ArgumentBuilder builder = new()
+                {
+                    { (option & RefsFilter.Heads) != 0, "refs/heads/" },
